/* eslint-disable react/jsx-pascal-case */
import React from "react";
import "./App.css";
import { Switch, Route, withRouter, Link } from "react-router-dom";
import { getDataAPI } from "./getDataAPI";
import FilterByGender from "../characterChoice2&3&4/filter_2";
import ChosenCharacter from "../characterChoice2&3&4/chosen_character_4";
import Wedding from "../wedding7/wedding_7";
import HotorNot from "../HotorNot/HotorNot.js";
import Homepage_1 from "../homepage1/homepage_1";
import Homepage_2 from "../homepage2/Splash";
import TurkeyWeigh from "../turkeyweigh/turkeyweigh";
import Credits from "../credits/credits";
import Button from "../auxiliaries/button";
import soundfile from "./StarWarsThemeJohnWilliams.mp3";

class App extends React.Component {
  constructor(props) {
    super(props);
    this.defineTurkeyWeight = this.defineTurkeyWeight.bind(this);
    this.state = {
      characters: [],
      chosenCharacter: {},
      url: soundfile,
      targetCookingTime: 0,
      turkeyWeight: 0
    };
  }

  FnGetDataAPI = async () => {
    let GetData = await getDataAPI();
    let APIcharacters = GetData;
    this.setState({ characters: APIcharacters });
  };

  componentDidMount() {
    this.FnGetDataAPI();
  }

  handleChosenCharacter = char => {
    this.setState({ chosenCharacter: char });
  };

  defineTurkeyWeight = () => {
    const turkeyWeight = Math.floor(Math.random() * 20) + 10;
    const cookingTime = turkeyWeight * 37;

    this.setState({
      turkeyWeight: turkeyWeight,
      targetCookingTime: cookingTime
    });
  };

  render() {
    return (
      <div className="App">
        <Switch>
          <Route
            exact
            path="/"
            render={() => (
              <>
                <Homepage_1
                  balloonMessage={<h1>Cooking perfection starts now!</h1>}
                />
                <Link to="/splash1">
                  <Button message="Play!" />
                </Link>
              </>
            )}
          />
          <Route
            exact
            path="/splash1"
            render={() => (
              <>
                <Homepage_2
                  balloonMessage={
                    <h1>
                      It's Christmas morning, when all through the house. Not a
                      creature was stirring, not even a mouse.
                    </h1>
                  }
                  image="https://res.cloudinary.com/edwardwatson/image/upload/v1574854519/TurkeyTimer/house_n7knxi.png"
                />
                <Link to="/splash2">
                  <Button message="Next" />
                </Link>
              </>
            )}
          />
          <Route
            exact
            path="/splash2"
            render={() => (
              <>
                <Homepage_2
                  balloonMessage={
                    <h1>
                      It's time to put the turkey in for the perfect Christmas
                      lunch for all the family.
                    </h1>
                  }
                  image="https://res.cloudinary.com/edwardwatson/image/upload/v1574855394/TurkeyTimer/woman-flowers-holidays-girl-6270_aaco00.jpg"
                />
                <Link to="/turkeyweigh">
                  <Button message="Let's cook!" />
                </Link>
              </>
            )}
          />

          <Route
            exact
            path="/turkeyweigh"
            render={() => (
              <>
                <TurkeyWeigh
                  weight={this.state.turkeyWeight}
                  time={this.state.cookingTime}
                  defineTurkeyWeight={this.defineTurkeyWeight}
                />
              </>
            )}
          />
          <Route
            exact
            path="/credits"
            render={() => <Credits url={this.state.url} />}
          />
        </Switch>
      </div>
    );
  }
}

export default withRouter(App);
