import React from "react";
import "./LeaderboardView.css";
import { Switch, Route, withRouter, Link } from "react-router-dom";

class LeaderboardPost extends React.Component {
  constructor(props) {
    super(props);
  }

  componentDidMount = () => {};

  onSubmit(e) {
    e.preventDefault();

    firebase
      .firestore()
      .collection("leaders")
      .add({
        name,
        streak: Number(this.props.streak)
      })
      .then(() => {
        setName("");
        setTime("");
      })
      .then(() => (window.location.href = "/"));
  }

  render() {
    return (
      <div className="container">
        <h1>Congratulations</h1>
        <h2>You made it onto the leaderboard.</h2>
        <form onSubmit={onSubmit()}>
          <div>
            <label>Name</label>
            <input
              type="text"
              maxLength="6"
              value={name}
              onChange={e => setName(e.currentTarget.value)}
            />
          </div>
          <div>
            <label>TIME</label>
            <input
              type="number"
              value={this.props.streak}
              readonly
              onChange={e => setStreak(e.currentTarget.value)}
            />
          </div>
          <button className="btn">
            <a>Submit</a>
          </button>
        </form>
        <Link to="/">
          <button className="buttonstyle">Submit</button>
        </Link>
      </div>
    );
  }
}

export default LeaderboardPost;
